import React, {useState} from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Create(){
    const navigate = useNavigate();
    
    const [nom,setNom] = useState('')
    const [prenom, setPrenom] = useState('')
    const [adresse, setAdresse] = useState('')

    const createMission = async(e)=>{
        e.preventDefault();
        const formData = new FormData();
        formData.append('nom', nom)
        formData.append('prenom', prenom)
        formData.append('adresse', adresse)

        console.log(formData)
        await axios.post('http://localhost:3001/users', formData)
        .then(({data})=>{
            console.log(data.message)
            navigate('/')
        }).catch(({response})=>{
            if (response.status ==422) {
                console.log(response.data.errors)
            } else {
                console.log(response.data.message)
            }
        })
    }

    return(
        <form onSubmit={createMission}>
            <table className="mx-3">
                <tr>
                    <td>
                        <label>nom</label>
                    </td>
                    <td>
                        <input type="text" name="nom" value={nom} style={{"width":"250%"}} className="my-3" required 
                            onChange={(e)=>{setNom(e.target.value)}}/>
                    </td>
                </tr>
                <tr>
                    <td>
                        <label>prenom</label>
                    </td>
                    <td>
                        <input type="date" name="prenom" value={prenom} style={{"width":"250%"}} className="my-3" required
                            onChange={(e)=>{setPrenom(e.target.value)}}/>
                    </td>
                </tr>
                <tr>
                    <td>
                        <label>adresse</label>
                    </td>
                    <td>
                        <input type="date" name="adresse" value={adresse} style={{"width":"250%"}} className="my-3" required
                            onChange={(e)=>{setAdresse(e.target.value)}}/>
                    </td>
                </tr>

                <tr>
                    <td>
                        <button type="submit" className="btn btn-warning">Create</button>               
                    </td>
                </tr>
            </table>                   
        </form>  
    )
}
