import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Liste() {
    const [mission, setMission] = useState([])
    useEffect(() => {
      fetchMission();
    },[]
    )
    const fetchMission = async () => {
      await axios.get('http://localhost:3001/users').then(({ data }) => {setMission(data)}) 
    }
    const deleteMission = async (id) => {
      await axios.delete('http://127.0.0.1:8000/api/mission/' + id)
      .then(({ data }) => {
        console.log(data.message)
        fetchMission();
      }).catch(({ response: { data } }) => {
        console.log(data.message)
      })
    }
    return(
      <div>
        <Link to='/create'>
          <button>Create Users</button>
        </Link>
        <table>
          <tr>
            <th>Nom</th>
            <th>Prenom</th>
            <th>Adresse</th>
            <th>Actions</th> 
          </tr>
          {
            mission.length > 0 && (
              mission.map((row,key)=>(
                <tr key={key}>
                  <td>{row.description}</td>
                  <td>{row.deadline}</td>
                  <td>{row.isCompleted}</td>
                  <td>{row.completedAt}</td>
                  <td>
                    <Link to={`/update/${row.id}`}>Update</Link>                  
                    <button onClick={() => deleteMission(row.id)}>Delete</button>
                  </td>       
                </tr>
              ))
            )
          }
        </table>  
      </div> 
    )
}
