import React, { useState ,useEffect} from "react";
import axios from "axios";
import { useNavigate ,useParams} from "react-router-dom";

export default function Edit() {

    const navigate = useNavigate();
    const { id } = useParams();

    const [nom,setNom] = useState('')
    const [prenom, setPrenom] = useState('')
    const [adresse, setAdresse] = useState('')

    useEffect(()=>{
        fetchMission();
    },[])

    const fetchMission = async() =>{
        await axios.get(`http://localhost:3001/users/${id}`)
            .then(({ data }) => {
                const { nom, prenom, adresse } = data.mission
                setNom(nom)
                setPrenom(prenom)
                setAdresse(adresse)
            }).catch(({ response: {data} }) => {
                console.log(data.message)
            })
    }

    const updateMission = async (e) => {
        e.preventDefault();
        const formData = new FormData();
        formData.append('_method', 'PATCH')
        formData.append('nom', nom)
        formData.append('prenom', prenom)
        formData.append('adresse', adresse)

        await axios.post('http://localhost:3001/users' + id, formData)
            .then(({ data }) => {
                console.log(data.message)
                navigate('/')
            }).catch(({ response }) => {
                if (response.status == 422) {
                    console.log(response.data.errors)
                } else {
                    console.log(response.data.message)
                }
            })
    }

    return (
        <form onSubmit={updateMission}>
        <table className="mx-3">
        <tr>
                    <td>
                        <label>nom</label>
                    </td>
                    <td>
                        <input type="text" name="nom" value={nom} className="my-3" required 
                            onChange={(e)=>{setNom(e.target.value)}}/>
                    </td>
                </tr>
                <tr>
                    <td>
                        <label>prenom</label>
                    </td>
                    <td>
                        <input type="text" name="prenom" value={prenom} className="my-3" required
                            onChange={(e)=>{setPrenom(e.target.value)}}/>
                    </td>
                </tr>
                <tr>
                    <td>
                        <label>adresse</label>
                    </td>
                    <td>
                        <input type="text" name="adresse" value={adresse} className="my-3" required
                            onChange={(e)=>{setAdresse(e.target.value)}}/>
                    </td>
                </tr>

                <tr>
                    <td>
                        <button type="submit" className="btn btn-secondary">Update</button>               
                    </td>
                </tr>
        </table>                  
    </form>   
    )
}
