import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

export default function Edit() {
    const navigate = useNavigate();
    const { id } = useParams();

    const [nom, setNom] = useState("");
    const [prenom, setPrenom] = useState("");
    const [adresse, setAdresse] = useState("");

    useEffect(() => {
        fetchUser();
    }, []);

    const fetchUser = async () => {
        try {
            const response = await axios.get(`http://localhost:3001/users/${id}`);
            const { nom, prenom, adresse } = response.data;
            setNom(nom);
            setPrenom(prenom);
            setAdresse(adresse);
        } catch (error) {
            console.error("Error fetching user:", error.message);
        }
    };

    const updateUser = async (e) => {
        e.preventDefault();
        const updatedUser = { nom, prenom, adresse };
        try {
            const response = await axios.patch(`http://localhost:3001/users/${id}`, updatedUser);
            console.log(response.data.message);
            navigate("/");
        } catch (error) {
            console.error("Error updating user:", error.message);
        }
    };

    return (
        <form onSubmit={updateUser}>
            <table className="mx-3">
                <tbody>
                    <tr>
                        <td>
                            Nom : <input type="text" value={nom} onChange={(e) => {setNom(e.target.value)}}/>
                        </td>
                    </tr>
          <tr>
            <td>
              <label>prenom</label>
            </td>
            <td>
              <input
                type="text"
                name="prenom"
                value={prenom}
                className="my-3"
                required
                onChange={(e) => {
                  setPrenom(e.target.value);
                }}
              />
            </td>
          </tr>
          <tr>
            <td>
              <label>adresse</label>
            </td>
            <td>
              <input
                type="text"
                name="adresse"
                value={adresse}
                className="my-3"
                required
                onChange={(e) => {
                  setAdresse(e.target.value);
                }}
              />
            </td>
          </tr>
        </tbody>
      </table>
      <button type="submit" className="btn btn-secondary">
        Update
      </button>
    </form>
  );
}
