import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

export default function Liste() {
    const [users, setUsers] = useState([])
    useEffect(() => {
        fetchUsers();
    },[])
    const fetchUsers = async () => {
        await axios.get('http://localhost:3001/users').then(({ data }) => {setUsers(data)}) 
    }
    return(
        <div>
            <Link to='/create'>
                <button>Create Users</button>
            </Link>
            <table>
                <tr>
                    <th>Nom</th>
                    <th>Prenom</th>
                    <th>Adresse</th>
                    <th>Actions</th> 
                </tr>
                {
                    users.length > 0 && (
                        users.map((row,key)=>(
                            <tr key={key}>
                                <td>{row.nom}</td>
                                <td>{row.prenom}</td>
                                <td>{row.adresse}</td>
                                <td>
                                    <Link to={`/edit/${row.id}`}>Update</Link>                  
                                </td>       
                            </tr>
                        ))
                    )
                }
            </table>  
        </div> 
    )
}
